import Transcription from "./Transcription"
import Handwriting from "./Handwiriting"
import Voice from "./Voice"

const Body = ({ selectBody }) => {

  const postTranscription = async (task) => {
    const res = await fetch('http://localhost:5000/tasks', {
      method: 'POST',
      mode : 'no-cors',
      headers: {
        'Accept': 'application/json',
        'Content-type': 'application/json',
      },
      body: JSON.stringify(task),
    })

   
  }


  switch (selectBody.text) {
    case 'Transcription':
      return (<Transcription onSubmit= {postTranscription}/>);
      break;
    case 'Handwriting Recognition':
      return (<Handwriting />)
      break;
    case 'Voice Recognition':
      return (<Voice />)
      break;
    default:
      return (<Transcription />);
  }
   
}

//We initialize the Body to the transcription part
Body.defaultProps = {
  selectBody: 'Transcription',
}

export default Body
